mapper.select:
SELECT USER_ID, EMAIL, NAME, PHONE, ROLE, PROVIDER, PROFILE_IMAGE, CREATED_AT
FROM USERS
WHERE USER_ID = #{userId}

SELECT USER_ID, EMAIL, PASSWORD, NAME, PHONE, ROLE, PROVIDER, PROFILE_IMAGE, CREATED_AT
FROM USERS
WHERE EMAIL = #{email}

SELECT USER_ID, EMAIL, NAME, PHONE, ROLE, PROVIDER, PROFILE_IMAGE, CREATED_AT
FROM USERS
WHERE PROVIDER = #{provider} AND PROVIDER_ID = #{providerId}

mapper.update:
UPDATE USERS
SET NAME = #{name},
    PHONE = #{phone},
    PROFILE_IMAGE = #{profileImage}
WHERE USER_ID = #{userId}

mapper.select:
SELECT USER_ID, EMAIL, NAME, PHONE, ROLE, PROVIDER, PROFILE_IMAGE, CREATED_AT
FROM USERS
WHERE PHONE = #{phone}
